    public static String numbersOnly(String field){
        if (!field.matches("[0-9-]+")) {
            return "Please enter numbers only.";
       }
        return "";
    }

    public static String lettersOnly(String param){
        if (!param.matches("[a-zA-Z_]+")) {
            return "Please enter letters only.";
        }
        return "";
    }

    public static String isEmpty(String param){
        if(param.isBlank()){
            return "Cannot be blank";
        }
        return "";
    }

